Implement Dock commands in HarvestBinCommands:

1. DockCommands.swift:
```swift
public extension Defaults {
    struct Dock {
        private let writer = DefaultsWriter()
        private let reader = DefaultsReader()

        // Icon size
        public func setIconSize(_ size: Int) async throws {
            guard size >= 16 && size <= 512 else {
                throw CommandError.validationFailed("Icon size must be between 16 and 512")
            }
            try await writer.setInt(size, forKey: "tilesize", domain: "com.apple.dock")
        }

        // Position
        public enum Position: String {
            case left, bottom, right
        }

        public func setPosition(_ position: Position) async throws {
            try await writer.setString(position.rawValue, forKey: "orientation", domain: "com.apple.dock")
        }

        // Auto-hide
        public func setAutoHide(_ enabled: Bool) async throws {
            try await writer.setBool(enabled, forKey: "autohide", domain: "com.apple.dock")
        }

        // Show recents
        public func setShowRecents(_ show: Bool) async throws {
            try await writer.setBool(show, forKey: "show-recents", domain: "com.apple.dock")
        }

        // Static only
        public func setStaticOnly(_ enabled: Bool) async throws {
            try await writer.setBool(enabled, forKey: "static-only", domain: "com.apple.dock")
        }

        // Minimize effect
        public enum MinimizeEffect: String {
            case genie, scale
        }

        public func setMinimizeEffect(_ effect: MinimizeEffect) async throws {
            try await writer.setString(effect.rawValue, forKey: "mineffect", domain: "com.apple.dock")
        }
    }
}
```

