Implement administrative commands that require sudo:

1. AdministrativeCommands.swift:
```swift
public struct AdministrativeCommands {
    // SSH Key Generation
    public struct SSHKeyCommand: CommandProtocol {
        public let keyType: KeyType
        public let outputPath: String
        public let comment: String?

        public enum KeyType: String {
            case rsa = "rsa"
            case ed25519 = "ed25519"
            case ecdsa = "ecdsa"
        }

        public var command: String { "ssh-keygen" }
        public var arguments: [String] {
            var args = ["-t", keyType.rawValue, "-f", outputPath, "-N", ""]
            if let comment = comment {
                args += ["-C", comment]
            }
            return args
        }
        public var requiresSudo: Bool { false }
        public var affectedProcess: String? { nil }

        public func validate() throws {
            let url = URL(fileURLWithPath: outputPath)
            guard !FileManager.default.fileExists(atPath: url.path) else {
                throw CommandError.validationFailed("Key file already exists")
            }
        }
    }

    // Auto-login configuration
    public struct AutoLoginCommand: CommandProtocol {
        public let username: String
        public let enabled: Bool

        public var command: String { "defaults" }
        public var arguments: [String] {
            if enabled {
                return ["write", "/Library/Preferences/com.apple.loginwindow", "autoLoginUser", username]
            } else {
                return ["delete", "/Library/Preferences/com.apple.loginwindow", "autoLoginUser"]
            }
        }
        public var requiresSudo: Bool { true }
        public var affectedProcess: String? { nil }
    }

    // Clear Dock icons
    public struct ClearDockCommand: CommandProtocol {
        public var command: String { "defaults" }
        public var arguments: [String] {
            ["write", "com.apple.dock", "persistent-apps", "-array"]
        }
        public var requiresSudo: Bool { false }
        public var affectedProcess: String? { "Dock" }
    }

    // Remote access
    public struct RemoteAccessCommand: CommandProtocol {
        public let service: Service
        public let enabled: Bool

        public enum Service: String {
            case ssh = "com.openssh.sshd"
            case vnc = "com.apple.screensharing"
        }

        public var command: String { "launchctl" }
        public var arguments: [String] {
            [enabled ? "load" : "unload", "-w", "/System/Library/LaunchDaemons/\(service.rawValue).plist"]
        }
        public var requiresSudo: Bool { true }
        public var affectedProcess: String? { nil }
    }
}
```

