2. CommandManager.swift:
```swift
@MainActor
class CommandManager: ObservableObject {
    @Published var categories: [CommandCategory] = []
    @Published var selectedCategory: CommandCategory?
    @Published var searchText = ""
    @Published var output: CommandOutput?
    @Published var isExecuting = false

    struct CommandCategory {
        let name: String
        let icon: String
        let commands: [DemoCommand]
    }

    init() {
        loadCommands()
    }

    func executeCommand(_ command: DemoCommand, parameters: [String: Any]) async {
        isExecuting = true
        do {
            let result = try await command.execute(with: parameters)
            output = CommandOutput(success: true, result: result)
        } catch {
            output = CommandOutput(success: false, error: error)
        }
        isExecuting = false
    }
}
```

